import {React,useState} from 'react'
import {Link,useNavigate} from "react-router-dom";

const Profile = () => {

    const [mobile,setMobile]=useState('');
    const [currentMobile,setCurrentMobile]=useState('');
    const [mobileEdit,setMobileEdit]=useState('hidden-mobile-edit')

    const changePhone=()=>{

         setMobile(currentMobile)
         setMobileEdit('hidden-mobile-edit')
    }

    const openEditor=()=>{

        setMobileEdit('mobile-edit')
    }


  return (
    <>

     <div className="main-profile">
        <h1>User name :{}</h1>

        <h1>Mobile Number :{mobile} <button className='update-button' onClick={openEditor}>Click to Update</button></h1> 
        <div className={mobileEdit}>
        <h1><label htmlFor="Name" className="update-mobile" >Update mobile number</label></h1><br/>
            <input type="text"  className="update-mobile" value={currentMobile}
        onChange={(e) => setCurrentMobile(e.target.value)} minlength="10" maxlength="10" required/> 
        <button className='update-button' onClick={changePhone}>Update</button>
        </div>

        <h1>Address :{}</h1>
     </div> 
    </>
  )
}

export default Profile
